import{Controller}from"@hotwired/stimulus";export default class extends Controller{static targets=["selected","output"];selectItem(t){t.preventDefault();const e=t.target.getAttribute("data-value");this.selectedTarget.textContent=e,this.outputTarget.textContent=e,this.outputTarget.value=e;document.getElementById("industry-hidden-field").value=e;this.element.querySelector('[data-industry-dropdown-target="selected"]').classList.add("hidden")}}