import{Controller}from"@hotwired/stimulus";export default class extends Controller{static targets=["jobCard"];connect(){this.changeCardColors()}changeCardColors(){this.jobCardTargets.forEach((o=>{const r=o.dataset.jobCardColor;o.classList.add(this.industryToColor(r))}))}industryToColor(o){return`bg-${{"Software Development":"yellow","Finance & Accounting":"blue",Architecture:"green","Marketing & Graphic Design":"red",Photography:"sky","Video & Film":"indigo",Fashion:"cyan"}[o]||"yellow"}-100`}}