import{Controller}from"@hotwired/stimulus";export default class extends Controller{static targets=["output","dropdown","hiddenField"];connect(){this.selectedTags=[]}toggleDropdown(){this.dropdownTarget.classList.toggle("hidden")}updateSelection(t){const e=t.target.value;t.target.checked?this.selectedTags.push(e):this.selectedTags=this.selectedTags.filter((t=>t!==e)),this.updateOutput(),this.updateHiddenField()}updateOutput(){0===this.selectedTags.length?this.outputTarget.textContent="Select Tags":this.outputTarget.textContent=this.selectedTags.join(", ")}updateHiddenField(){this.hiddenFieldTarget.value=JSON.stringify(this.selectedTags)}}